import "./MoviePage.css";
import PosterCard from "./PosterCard"
import InfoCard from "./InfoCard"
import axios from 'axios';
import React, {useEffect, useState} from 'react';

let MOVIEDATA_ARRAY = [];
const ratingsData = [];

const MoviePage = (props) => {
    const [movieData, setMovieData] = useState([]);

   

    const getHandler = async () =>{
        try{
        axios.get('https://www.omdbapi.com/?i=' + props.id + '&apikey=bf994a28')
        .then(res => {
            MOVIEDATA_ARRAY = res.data;
            setMovieData(MOVIEDATA_ARRAY);
            ratingsData = MOVIEDATA_ARRAY.Ratings;
            console.log(MOVIEDATA_ARRAY.Ratings);
        })
        .catch(err => {
            console.log(err);
          })
        } catch (error){
            console.log('fail');
        }
    }


    useEffect(() => {
        getHandler();
    },[])

  return (
    <div className="moviePageContainer">
      <PosterCard key={props.id} id={props.id} title={movieData.Title} plot={movieData.Plot} poster={movieData.Poster} year={movieData.Released} />
      { <ul>
      {MOVIEDATA_ARRAY.Ratings.map((movie) => (
              <InfoCard
                key={props.id}
                title={movie.Source}
                rating={movie.Value}
              />
            ))} 
      </ul> }
    </div>
  );
};

export default MoviePage;
