import "./MoviePage.css";
import PosterCard from "./PosterCard";
import InfoCard from "./InfoCard";
import axios from "axios";
import React, { useEffect, useState } from "react";

let MOVIEDATA_ARRAY = [];
let RATINGSDATA_ARRAY = [];
let IIWW_RATING = 0;
let imdbval = 0;
let rotval = 0;
let metvalArray = [];

let imdbR = false;
let rotR = false;
let metR = false;


const MoviePage = (props) => {
  const [movieData, setMovieData] = useState([]);
  const [ratingsData, setRatingsData] = useState([]);
  const [iiwwRating, setiiwwRating] = useState(0);
  const [iiwwStyle, setiiwwStyle] = useState('');
  const [infoCardText, setInfoCardText] = useState('');

  const getHandler = async () => {
    try {
      axios
        .get("https://www.omdbapi.com/?i=" + props.id + "&apikey=bf994a28")
        .then((res) => {
          MOVIEDATA_ARRAY = res.data;
          setMovieData(MOVIEDATA_ARRAY);
          RATINGSDATA_ARRAY = MOVIEDATA_ARRAY.Ratings;
          setRatingsData(RATINGSDATA_ARRAY);
          createIIWWRating();
          
        })
        .catch((err) => {
          console.log(err);
        });
    } catch (error) {
      console.log("fail");
    }
  };

  useEffect(() => {
    getHandler();
  },[]);

  const createIIWWRating = () =>{
      ratingsData.map((movie) => {
          if (movie.Source === "Internet Movie Database"){
            const imdbvalArray = movie.Value.split("/");
            imdbval = imdbvalArray[0] * 10;
            imdbR = true;
          }

          if (movie.Source === "Rotten Tomatoes"){
              rotval = movie.Value.replace("%", "");
              rotR = true;
          }

          if (movie.Source === "Metacritic"){
              metvalArray = movie.Value.split("/");
              metR = true;
          }
        })

          if (imdbR === true && rotR === false && metR === false){
            IIWW_RATING = parseInt(IIWW_RATING) + parseInt(imdbval);
            imdbR = false;
          } else if (imdbR === true && rotR === true && metR === false){
            IIWW_RATING = parseInt(IIWW_RATING) + parseInt(imdbval*0.6);
            IIWW_RATING = parseInt(IIWW_RATING) + parseInt(rotval*0.4);
            imdbR = false;
            rotR = false;
          } else if (imdbR === true && rotR === false && metR === true){
            IIWW_RATING = parseInt(IIWW_RATING) + parseInt(imdbval*0.6);
            IIWW_RATING = parseInt(IIWW_RATING) + parseInt(metvalArray[0]*0.4);
            imdbR = false;
            metR = false;
          } else if (imdbR === true && rotR === true && metR === true){
            IIWW_RATING = parseInt(IIWW_RATING) + parseInt(imdbval*0.6);
            IIWW_RATING = parseInt(IIWW_RATING) + parseInt(rotval*0.2);
            IIWW_RATING = parseInt(IIWW_RATING) + parseInt(metvalArray[0]*0.2);
            imdbR = false;
            rotR = false;
            metR = false;

            setiiwwRating(IIWW_RATING + "/100");

            if (IIWW_RATING > "70"){
                setiiwwStyle("card text-white bg-success mb-3");
                //setInfoCardText("Yes it is!");
            }else{
                setiiwwStyle("card text-white bg-danger mb-3");
                //setInfoCardText("No, don't waste your time!");
            }
          }

  }

  return (
    <div className="moviePageContainer">
      <PosterCard
        key={props.id}
        id={props.id}
        title={movieData.Title}
        plot={movieData.Plot}
        poster={movieData.Poster}
        year={movieData.Released}
      />
      <ul className="ratingsContainer">
        <InfoCard style={iiwwStyle} title="Is It Worth Watching?" rating={iiwwRating} text={infoCardText} /> 
        {ratingsData.map((movie) => (
          <InfoCard style="card text-white bg-dark mb-3" title={movie.Source} rating={movie.Value} />
        ))}
      </ul>
    </div>
  );
};

export default MoviePage;
