import "./Card.css";
import React, { useState, useEffect } from 'react'

const Card = (props) => {
    const [poster,setPoster] = useState(props.poster);

    const posterCheck = () =>{
        if (poster === 'N/A'){
            setPoster("https://i.mydramalist.com/0Ap1Yc.jpg?v=1");
        }
    }

    useEffect(() => {
        posterCheck();
    },[])

  return (
    <div className="card">
      <img
        src={poster}
        className="card-img-top"
      ></img>
      <div className="card-body">
        <p className="card-text">{props.title} ({props.year})</p>
      </div>
    </div>
  );
};

export default Card;
