import SearchForm from "./SearchForm";
import Card from "./Card";
import "./StartPage.css";
import React, {useState} from 'react';
import axios from 'axios';

let DUMMY_LIST = [];

const StartPage = (props) => {
    const [movieData, setMovieData] = useState([]);
    const [enteredMovie, setEnteredMovie] = useState('');

    const getHandler = async (name) =>{
        try{
        axios.get('https://www.omdbapi.com/?s=' + name + '&apikey=bf994a28')
        .then(res => {
            if (res.data.Response === "True"){
                DUMMY_LIST = res.data.Search;
                setMovieData(DUMMY_LIST);
                console.log(movieData);

            } else {
                console.log("Movies not found.");
                setMovieData([]);
            }
        })
        .catch(err => {
            console.log(err);
          })
        } catch (error){
            console.log('fail');
        }
    }

    const movieSearchHandler = (search) =>{
        let searchName = "";
        searchName = search.replace(" ","+");
        getHandler(searchName);
    }

    const movieSelectHandler = (id) =>{
        props.movieSelected(id);
    }

  return (
    <div>
      <SearchForm handler={movieSearchHandler} search={movieSearchHandler}/>
      <div class="row">
    <div class="col-sm-12">
      <ul className="listContainer">
        {movieData.map((movie) => (
          <Card key={movie.imdbID} id={movie.imdbID} title={movie.Title} poster={movie.Poster} year={movie.Year} cardClick={movieSelectHandler}/>
        ))}
      </ul>
      </div>
      </div>
    </div>
  );
};

export default StartPage;
