import SearchForm from "./SearchForm";
import Card from "./Card";
import "./StartPage.css";
import React, {useState} from 'react';
import axios from 'axios';

let DUMMY_LIST = [];

const StartPage = () => {
    const [movieData, setMovieData] = useState([]);
    const buttonHandler = async () =>{
        try{
        axios.get('https://www.omdbapi.com/?s=guardians+of+the+galaxy&apikey=bf994a28')
        .then(res => {
            DUMMY_LIST = res.data.Search;
            setMovieData(DUMMY_LIST);
        })
        } catch (error){
            //error handler
        }
    }

  return (
    <div>
      <SearchForm handler={buttonHandler}/>
      <ul>
        {DUMMY_LIST.map((movie) => (
          <Card title={movie.Title} key={movie.imdbID} />
        ))}
      </ul>
    </div>
  );
};

export default StartPage;
