import "./Card.css";

const Card = (props) => {
  return (
    <div className="card">
      <img
        src={props.Poster}
        className="card-img-top"
      ></img>
      <div className="card-body">
        <p className="card-text">{props.title} ({props.Year})</p>
      </div>
    </div>
  );
};

export default Card;
