  const [movieID, setMovieID] = useState('');
  const [page, setPage] = useState(<StartPage movieSelected={pageHandler}/>);

  const pageHandler = (id) => {
    setMovieID(id);
    setPage(<MoviePage id={movieID}/>)
  }

  return (
    <div className="App">
      {page}
    </div>
  );
}

export default App;
